import { SSOOIDCClient, CreateTokenWithIAMCommand } from "@aws-sdk/client-sso-oidc";

const client = new SSOOIDCClient({
    region: "us-west-2",  // Specify your region
});

const input = {
    clientId: "STRING_VALUE", // Replace with your client ID
    grantType: "authorization_code", // or "refresh_token", depending on your use case
    code: "STRING_VALUE",  // This is for authorization code grant
    refreshToken: "STRING_VALUE", // This is for refresh token grant
    redirectUri: "STRING_VALUE",  // Your redirect URI for authorization code grant
};

const command = new CreateTokenWithIAMCommand(input);
try {
    const data = await client.send(command);
    console.log("Token data:", data);
} catch (error) {
    console.error("Error:", error);
}
